import React, { useEffect, useState } from "react";
import s from './Main.module.css'

import '../../css/bootstrap.min.css';


const Main = () => {

	const [data, setData] = useState('')
	const [data2, setData2] = useState([])

    useEffect(() => {
		getDisciplina()
        getProfiles()
    }, []);

    let getProfiles = async () => {
        let response = await fetch('https://def6dcc9-b637-4e72-975e-989d3170335e.mock.pstmn.io/getNumberOfAssessedProfiles')
        let data = await response.json()
        setData(data)
    }

	let getDisciplina = async () => {
        let response = await fetch('https://def6dcc9-b637-4e72-975e-989d3170335e.mock.pstmn.io/getNumberOfAssessedDisciplines')
        let data2 = await response.json()
        setData2(data2)
    }

	return (
		<div class="">
			  <main class="col-md-9 ms-sm-auto col-lg-10 px-md-4">


				<div class="row">
					<div class="col-md-6">
						<div class="row g-0 border text-white rounded bg-primary overflow-hidden flex-md-row mt-2 mb-2 shadow-sm h-md-250 position-relative">
							<div class="col p-4 d-flex flex-column position-static">
								<h3 class="mb-0">Профиль</h3>
							</div>
						</div>
					</div>
					<div class="col-md-6" >
						<div class="row g-0 border text-white rounded bg-success overflow-hidden flex-md-row mt-2 mb-2 shadow-sm h-md-250 position-relative">
							<div class="col p-4 d-flex flex-column position-static">
							<button type="button" class="btn btn-outline-light" href="/">
								<h3 class="mb-0">Дисциплина</h3>
								</button>
							</div>
						</div>
					</div>
				</div>



				<div class="row">
					<div class="col-md-6">
						<div class="row g-0 border rounded overflow-hidden flex-md-row mt-2 mb-2 shadow-sm h-md-250 position-relative p-4">
							<h3>Сколько всего студентов прошедших анкетирование</h3>
							<div className={s.main2}>
								<div className="main3">
									<h5>Кол-во профилей по уч плану: {data.total}</h5>
									<h5>Кол-во профилей, которые оценили студенты: {data.count}</h5>
								</div>
							</div>
						</div>
					</div>
					<div class="col-md-6">
						<div class="row g-0 border rounded overflow-hidden flex-md-row mt-2 mb-2 shadow-sm h-md-250 position-relative p-4">
							<h3>Количество оцененных дисциплин</h3>
							<div className={s.main2}>
								<div className={s.main3}>
									<h5>Кол-во дисциплин по уч плану: {data2.total}</h5>
									<h5>Кол-во дисциплин, которые оценили студенты: {data2.count}</h5>
								</div>
							</div>
						</div>
					</div>
				</div>

			</main>






		</div >
	);
}

export default Main;
