import './App.css';

import Menu from './components/Menu';
import {BrowserRouter, Route, Routes} from "react-router-dom";
import Profil2 from './components/pages/Vitievskii/Profil2';
import Disciplina2 from './components/pages/Kurmaev/Disciplina2';
import Ugadiarov from './components/pages/Ugadiarov/Ugadiarov';
import Chart from "react-apexcharts";
import { useEffect, useState } from 'react';

function App() {

  const [year, setYear] = useState(localStorage.getItem('year') || 2018);

  useEffect(() => {
    const storageListener = () => {
      const newYear = localStorage.getItem('year');
      if (newYear !== year) {
        setYear(newYear);
      }
    };

    window.addEventListener('storage', storageListener);

    return () => {
      window.removeEventListener('storage', storageListener);
    };
  }, [year]);

  return (
    <div>
   
      <div className='row'>
        <Menu/>
        <div className=''>
          <BrowserRouter>
            <Routes>
              <Route path='/' element={<Ugadiarov />} />
              <Route path='/profil' element={<Profil2 />} year={year} />
             
              <Route path='/disciplina' element={<Disciplina2 />} year={year} />
            </Routes>
          </BrowserRouter>
        </div>
      </div>
    </div>
  );
}

export default App;
